import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import CommentBankRoundedIcon from '@mui/icons-material/CommentBankRounded';
import SendOutlinedIcon from '@mui/icons-material/SendOutlined';
import "./shortsfeed.css";
import { useParams } from "react-router-dom";

const ShortsFeed = () => {
  const { id } = useParams(); // Get video ID from URL (if available)
  console.log("user id: ")
  console.log(id);
  const [shorts, setShorts] = useState([]);
  const videoRefs = useRef({}); // Store refs as an object

  const getTokenFromCookie = () => {
    const cookies = document.cookie;
    const cookieArray = cookies.split(';');
    const tokenCookie = cookieArray.find(cookie => cookie.trim().startsWith('token='));
    if (tokenCookie) {
      const token = tokenCookie.split('=')[1];
      return token;
    }
    return null;
  }; 
  
  const updateShortsLike = async (videoId) => {
    try {
      await axios.patch(`http://localhost:4000/api/shortlikes/${id}`);
    } catch (error) {
      console.error("Likes error:", error.response?.data || error.message);
    }
  };
  
  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const res = await axios.get("http://localhost:4000/api/allshorts");
        if (res.data.success && res.data.videos) {
          setShorts(res.data.videos);
        } else {
          throw new Error("Invalid response structure");
        }
      } catch (err) {
        console.error("Error fetching videos:", err);
      }
    };
    fetchVideos();
  }, []);

  // ✅ Auto-play one video at a time
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          const video = entry.target;
          if (entry.isIntersecting) {
            video.play().catch((error) => console.log("Autoplay blocked:", error));
          } else {
            video.pause();
            video.currentTime = 0;
          }
        });
      },
      { threshold: 0.8 } // Play when 80% visible
    );

    Object.values(videoRefs.current).forEach((video) => {
      if (video) observer.observe(video);
    });

    return () => observer.disconnect();
  }, [shorts]);

  return (
    <div className="shorts-feed">
      {shorts.map((short) => (
        <motion.div
          key={short._id}
          className="shorts-card"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <video
            ref={(el) => (videoRefs.current[short._id] = el)}
            className="shorts-video"
            src={short.videoLink}
            loop
            playsInline
            controls
          ></video>
          <div className="video-details">
            <h3>{short.title}</h3>
            <p>{short.hashtags ? short.hashtags.join(" ") : ""}</p>
          </div>
          <div className="channel-info">
            <img src={short.channelProfile} alt="Profile" />
            <h4>{short.channelName}</h4>
            <button className="follow-btn">Follow</button>
          </div>
          <div className="action-buttons">
            <div className="like-btn" onClick={() => updateShortsLike(short._id)}>
              <FavoriteBorderIcon />
            </div>
            <CommentBankRoundedIcon />
            <SendOutlinedIcon />
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default ShortsFeed;
