import React, { useState } from 'react';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';
import axios from 'axios';

const SearchBar = ({ setVideos }) => {
  const [query, setQuery] = useState('');
  const { transcript, listening, resetTranscript } = useSpeechRecognition();

  if (!SpeechRecognition.browserSupportsSpeechRecognition()) {
    return <span>Your browser does not support voice recognition.</span>;
  }

  // Handle voice search
  const handleVoiceSearch = async () => {
    if (transcript) {
      setQuery(transcript);
      searchVideos(transcript);
    }
  };

  // Handle text search
  const handleTextSearch = async () => {
    searchVideos(query);
  };

  // Search function
  const searchVideos = async (searchQuery) => {
    try {
      const response = await axios.get(`/api/videos/search`, { params: { query: searchQuery } });
      setVideos(response.data.videos);
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  return (
    <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
      <button onClick={SpeechRecognition.startListening}>üé§</button>
      <button onClick={SpeechRecognition.stopListening}>‚èπÔ∏è</button>
      <button onClick={resetTranscript}>üîÑ</button>
      <input
        type="text"
        value={query || transcript}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search videos"
        style={{ padding: '10px', width: '300px' }}
      />
      <button onClick={handleTextSearch}>Search</button>
      <button onClick={handleVoiceSearch}>Voice Search</button>
    </div>
  );
};

export default SearchBar;
