import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import CommentBankRoundedIcon from '@mui/icons-material/CommentBankRounded';
import SendOutlinedIcon from '@mui/icons-material/SendOutlined';
import "./shortsfeed.css";

const ShortsFeed = () => {
  const [shorts, setShorts] = useState([]);
  const videoRefs = useRef([]);
  
  const updateLike = async () => {
    try {
        await axios.patch(`http://localhost:4000/api/likes/${id}`);
    } catch (error) {
        console.error('Likes error:', error.response?.data || error.message);
    }
  };
  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const res = await axios.get('http://localhost:4000/api/allshorts');
        if (res.data.success && res.data.videos) {
          setShorts(res.data.videos);
        } else {
          throw new Error('Invalid response structure');
        }
      } catch (err) {
        console.error('Error fetching videos:', err);
      }
    };
    fetchVideos();
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          const video = entry.target;
          if (entry.isIntersecting) {
            video.play();  // Play when in viewport
          } else {
            video.pause(); // Pause when out of viewport
            video.currentTime = 0; // Reset video
          }
        });
      },
      { threshold: 0.8 } // Video should be 80% visible to play
    );

    videoRefs.current.forEach((video) => {
      if (video) observer.observe(video);
    });

    return () => observer.disconnect();
  }, [shorts]);

  return (
    <div className="shorts-feed">
      {shorts.map((short, index) => (
        <motion.div key={short._id} 
          className="shorts-card"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <video 
            ref={(el) => (videoRefs.current[index] = el)}
            className="shorts-video" 
            src={short.videoLink}
            loop 
            playsInline
            controls
          ></video>
          <div className="video-details">
            <h3>{short.title}</h3>
            <p>{short.hashtags ? short.hashtags.join(" ") : ""}</p>
          </div>
          <div className="channel-info">
            <img src={short.channelProfile} alt="Profile" />
            <h4>{short.channelName}</h4>
            <button className="follow-btn">Follow</button>
          </div>
          <div className="action-buttons">
            <FavoriteBorderIcon/>
            <CommentBankRoundedIcon/>
            <SendOutlinedIcon/>
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default ShortsFeed;
