import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const LikedVideo = ({ sideNavbar }) => {
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchLikedVideos = async () => {
      try {
        const userId = getTokenFromCookie();
            
        const response = await axios.get('http://localhost:4000/auth/likedVideo', {
            withCredentials: true,
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${userId}`,
            },
        });
        setVideos(response.data.likedVideos || []);
      } catch (error) {
        console.error('Failed to fetch liked videos:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchLikedVideos();
  }, []);

  return (
    <div className={sideNavbar ? 'searchPage' : 'fullHomePage'}>
      <div className="homePage_options">
        <div className="homePage_option">Welcome to Video Platform</div>
      </div>
      <div className={sideNavbar ? 'search_mainPage' : 'fullHome_mainPage'}>
        {loading ? (
          <div>Loading liked videos...</div>
        ) : videos.length > 0 ? (
          videos.map((item) => (
            <Link key={item._id} to={`/watch/${item._id}`} className="youtube_Video">
              <div className="youtube_thumbnailBox">
                <img
                  src={item?.videoId?.thumbnail}
                  alt="Thumbnail"
                  className="youtube_thumbnailPic"
                />
                <div className="youtube_timingThumbnail">28:05</div>
              </div>
              <div className="youtubeTitleBox">
                <div className="youtubeTitleBoxProfile">
                  <img
                    src={item?.user?.profilePic}
                    alt="Profile"
                    className="youtube_thumbnail_profile"
                  />
                </div>
                <div className="youtubeTitleBox_title">
                  <div className="youtube_videoTitle">{item?.videoId?.title || 'Untitled'}</div>
                  <div className="youtube_channelName">{item?.user?.channelName || 'Unknown Channel'}</div>
                  <div className="youtube_videoViews">
                    <p>Watched on: {new Date(item.watchedAt).toLocaleString()}</p>
                  </div>
                </div>
              </div>
            </Link>
          ))
        ) : (
          <div>No liked videos available</div>
        )}
      </div>
    </div>
  );
};

export default LikedVideo;
