import React, { useState, useEffect } from "react";
import axios from "axios";
import { FaHeart, FaComment, FaShare } from "react-icons/fa";
import { motion } from "framer-motion";
import './shortsfeed.css';

const ShortsFeed = () => {
  const [shorts, setShorts] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/shorts")
      .then((response) => setShorts(response.data))
      .catch((error) => console.error(error));
  }, []);

  return (
    <div className="h-screen w-full overflow-y-scroll snap-y snap-mandatory bg-black">
      {shorts.map((short) => (
        <motion.div key={short._id} 
          className="relative w-full h-screen flex items-center justify-center snap-start"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <video 
            className="absolute w-full h-full object-cover" 
            src={short.videoUrl} 
            loop 
            autoPlay 
            muted 
          ></video>
          <div className="absolute bottom-10 left-4 text-white">
            <h3 className="font-bold">{short.title}</h3>
            <p className="text-sm">{short.hashtags.join(" ")}</p>
          </div>
          <div className="absolute top-4 left-4 flex items-center space-x-2">
            <img src={short.channelProfile} className="w-10 h-10 rounded-full" alt="Profile" />
            <h4 className="text-white font-bold">{short.channelName}</h4>
            <button className="px-3 py-1 bg-red-500 text-white rounded-lg">Follow</button>
          </div>
          <div className="absolute right-4 bottom-20 flex flex-col space-y-4 text-white text-xl">
            <button><FaHeart /></button>
            <button><FaComment /></button>
            <button><FaShare /></button>
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default ShortsFeed;
