import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import CommentBankRoundedIcon from '@mui/icons-material/CommentBankRounded';
import SendOutlinedIcon from '@mui/icons-material/SendOutlined';
import "./shortsfeed.css";
import { useParams } from "react-router-dom";
import FavoriteRoundedIcon from '@mui/icons-material/FavoriteRounded';

const ShortsFeed = () => {
  const { id: initialId } = useParams();
  
  const [shorts, setShorts] = useState([]);
  const videoRefs = useRef({});
  const [isLiked, setIsLiked] = useState(false);
  const [activeVideoId, setActiveVideoId] = useState(initialId || null);

  const getTokenFromCookie = () => {
    const cookies = document.cookie;
    const tokenCookie = cookies.split(';').find(cookie => cookie.trim().startsWith('token='));
    return tokenCookie ? tokenCookie.split('=')[1] : null;
  };

  const islike = async (videoId) => {
    try {
      const userId = getTokenFromCookie();
      const response = await axios.get(`http://localhost:4000/auth/isliked/${videoId}`, {
        withCredentials: true,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${userId}`,
        },
      });
      if (response.status === 200) {
        setIsLiked(response.data.Liked);
      }
    } catch (error) {
      console.error('Liked error:', error.response?.data || error.message);
    }
  };

  
  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const res = await axios.get("http://localhost:4000/api/allshorts");
        if (res.data.success && res.data.videos) {
          setShorts(res.data.videos);
        } else {
          throw new Error("Invalid response structure");
        }
      } catch (err) {
        console.error("Error fetching videos:", err);
      }
    };
    fetchVideos();
  }, []);

  useEffect(() => {
    if (activeVideoId) {
      islike(activeVideoId);
    }
  }, [activeVideoId]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          const video = entry.target;
          if (entry.isIntersecting) {
            video.play().catch((error) => console.log("Autoplay blocked:", error));
            setActiveVideoId(video.getAttribute("data-id")); // Set active video ID
          } else {
            video.pause();
            video.currentTime = 0;
          }
        });
      },
      { threshold: 0.8 }
    );

    Object.values(videoRefs.current).forEach((video) => {
      if (video) observer.observe(video);
    });

    return () => observer.disconnect();
  }, [shorts]);

  return (
    <div className="shorts-feed">
      {shorts.map((short) => (
        <motion.div
          key={short._id}
          className="shorts-card"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <video
            ref={(el) => (videoRefs.current[short._id] = el)}
            className="shorts-video"
            data-id={short._id} // Store video ID in data attribute
            src={short.videoLink}
            loop
            playsInline
            controls
          ></video>
          <div className="video-details">
            <h3>{short.title}</h3>
            <p>{short.hashtags ? short.hashtags.join(" ") : ""}</p>
          </div>
          <div className="channel-info">
            <img src={short.channelProfile} alt="Profile" />
            <h4>{short.channelName}</h4>
            <button className="follow-btn">Follow</button>
          </div>
          <div className="action-buttons">
            <div className="like-btn">
              {isLiked ? <FavoriteRoundedIcon /> : <FavoriteBorderIcon />}
            </div>
            <CommentBankRoundedIcon />
            <SendOutlinedIcon />
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default ShortsFeed;
