import React, { useState, useEffect } from "react";
import axios from "axios";
import { FaHeart, FaComment, FaShare } from "react-icons/fa";
import { motion } from "framer-motion";
import "./shortsfeed.css";

const ShortsFeed = () => {
  const [shorts, setShorts] = useState([]);
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);


  
  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const res = await axios.get('http://localhost:4000/api/allvideo');
        if (res.data.success && res.data.videos) {
          set(res.data.videos);
        } else {
          throw new Error('Invalid response structure');
        }
      } catch (err) {
        console.error('Error fetching videos:', err);
        setError('Failed to fetch videos');
      } finally {
        setLoading(false);
      }
    };

    fetchVideos();
  }, []);
  useEffect(() => {
    axios.get("http://localhost:5000/api/shorts")
      .then((response) => setShorts(response.data))
      .catch((error) => console.error(error));
  }, []);

  return (
    <div className="shorts-feed">
      {shorts.map((short) => (
        <motion.div key={short._id} 
          className="shorts-card"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <video 
            className="shorts-video" 
            src='https://res.cloudinary.com/djviiata9/image/upload/v1734079819/thumbnails/kcdk92xxgfb3wrlq8x43.jpg' 
            loop 
            autoPlay 
            muted 
          ></video>
          <div className="video-details">
            <h3>{short.title}</h3>
            <p>{short.hashtags.join(" ")}</p>
          </div>
          <div className="channel-info">
            <img src={short.channelProfile} alt="Profile" />
            <h4>{short.channelName}</h4>
            <button className="follow-btn">Follow</button>
          </div>
          <div className="action-buttons">
            <button><FaHeart /></button>
            <button><FaComment /></button>
            <button><FaShare /></button>
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default ShortsFeed;
