import React, { useState } from 'react';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';

const SearchBar = () => {
  const { transcript, listening, resetTranscript } = useSpeechRecognition();

  if (!SpeechRecognition.browserSupportsSpeechRecognition()) {
    return <span>Your browser does not support voice recognition.</span>;
  }

  const startListening = () => {
    SpeechRecognition.startListening({ continuous: true });
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h2>Voice Recognition Test</h2>
      <p>Click on the mic, speak, and see your sentences displayed below:</p>
      <div>
        <button onClick={startListening} style={{ marginRight: '10px' }}>üé§ Start Listening</button>
        <button onClick={SpeechRecognition.stopListening} style={{ marginRight: '10px' }}>‚èπÔ∏è Stop Listening</button>
        <button onClick={resetTranscript}>üîÑ Reset</button>
      </div>
      <div style={{ marginTop: '30px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px', width: '50%', margin: '20px auto', background: '#f9f9f9' }}>
        <strong>Listening:</strong> {listening ? "Yes" : "No"}
      </div>
      <div style={{ marginTop: '10px', fontSize: '18px', color: '#333' }}>
        <strong>Transcript:</strong>
        <p>{transcript || "Start speaking to see your speech here..."}</p>
      </div>
    </div>
  );
};

export default SearchBar;
