import React, { useState } from 'react';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';

const SearchBar = () => {
  const [sentences, setSentences] = useState([]); // To store sentences
  const { transcript, listening, resetTranscript } = useSpeechRecognition();

  if (!SpeechRecognition.browserSupportsSpeechRecognition()) {
    return <span>Your browser does not support voice recognition.</span>;
  }

  const startListening = () => {
    SpeechRecognition.startListening({ continuous: true });
  };

  const handleAddSentence = () => {
    if (transcript.trim()) {
      setSentences((prevSentences) => [...prevSentences, transcript]); // Save current transcript
      resetTranscript(); // Clear the transcript for the next sentence
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h2>Voice Recognition with Sentence Display</h2>
      <p>Click on the mic, speak, and your sentences will appear below:</p>
      <div>
        <button onClick={startListening} style={{ marginRight: '10px' }}>üé§ Start Listening</button>
        <button onClick={SpeechRecognition.stopListening} style={{ marginRight: '10px' }}>‚èπÔ∏è Stop Listening</button>
        <button onClick={handleAddSentence} style={{ marginRight: '10px' }}>‚úîÔ∏è Save Sentence</button>
        <button onClick={resetTranscript}>üîÑ Reset</button>
      </div>
      <div style={{ marginTop: '20px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px', width: '50%', margin: '20px auto', background: '#f9f9f9' }}>
        <strong>Listening:</strong> {listening ? "Yes" : "No"}
      </div>
      <div style={{ marginTop: '20px', fontSize: '18px', color: '#333' }}>
        <strong>Current Transcript:</strong>
        <p>{transcript || "Start speaking to see your speech here..."}</p>
      </div>
      <div style={{ marginTop: '30px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px', width: '60%', margin: '20px auto', background: '#e8f4fa' }}>
        <h3>Spoken Sentences:</h3>
        {sentences.length > 0 ? (
          <ul style={{ textAlign: 'left', listStyleType: 'disc' }}>
            {sentences.map((sentence, index) => (
              <li key={index} style={{ marginBottom: '10px', fontSize: '16px' }}>{sentence}</li>
            ))}
          </ul>
        ) : (
          <p>No sentences saved yet.</p>
        )}
      </div>
    </div>
  );
};

export default SearchBar;
